import { useEffect, useMemo, useState, useCallback } from 'react';
import * as anchor from '@project-serum/anchor';

import styled from 'styled-components';
import { Container, Snackbar, makeStyles, Box,
	List,
	ListItem,
	Typography, 
  Theme,
  AppBar,
  Toolbar, IconButton, Icon, Link, Drawer, Divider, Grid, Accordion, AccordionSummary, AccordionDetails
} from '@material-ui/core';
import MenuIcon from "@material-ui/icons/Menu"
import Twitt from "@material-ui/icons/Twitter"
import You from "@material-ui/icons/YouTube"
import Tel from "@material-ui/icons/Telegram"
import Chevron from "@material-ui/icons/ChevronLeft"
import Paper from '@material-ui/core/Paper';
import Alert from '@material-ui/lab/Alert';
import ExpandMoreIcon from "@material-ui/icons/ExpandMore"
import Fade from "react-reveal/Fade"
import { useWallet } from '@solana/wallet-adapter-react';
import { WalletDialogButton } from '@solana/wallet-adapter-material-ui';
import {
  awaitTransactionSignatureConfirmation,
  CandyMachineAccount,
  CANDY_MACHINE_PROGRAM,
  getCandyMachineState,
  mintOneToken,
} from './candy-machine';
import { AlertState, formatNumber, toDate } from './utils';
import { MintCountdown } from './MintCountdown';
import { GatewayProvider } from '@civic/solana-gateway-react';
import { PublicKey } from '@solana/web3.js';
import { MintButton } from './MintButton';

const ConnectButton = styled(WalletDialogButton)`
  width: 100%;
  height: 60px;
  margin-top: 10px;
  margin-bottom: 5px;
  background: linear-gradient(180deg, #604ae5 0%, #813eee 100%);
  color: white;
  font-size: 16px;
  font-weight: bold;
`;

export interface HomeProps {
  candyMachineId?: anchor.web3.PublicKey;
  connection: anchor.web3.Connection;
  startDate: number;
  txTimeout: number;
  rpcHost: string;
}
const MintContainer = styled.div``

let useStyles = makeStyles((theme: Theme) => ({
	app: {
		width: "100%",
	},
	list: {
		display: "flex",
		flexGrow: 1,
	},
	listD: {
		display: "flex",
		margin: "15px auto !important",
		flexDirection: "column",
		height: "35px",
		width: "100px",
		textAlign: "center",
	},
	drawerHeader: {
		display: "flex",
		alignItems: "center",
		padding: theme.spacing(0, 1),
		...theme.mixins.toolbar,
		justifyContent: "flex-end",
	},
	drawerPaper: {
		width: "100%",
		backgroundColor: "rgba(55,55,55, .6)",
		backdropFilter: "blur(2px)",
	},
	first: {
    height: "100vh",
    backgroundColor: "#000",
		/* background:
			"radial-gradient(circle, rgba(2,1,9,1) 0%, rgba(43,22,49,1) 50%, rgba(2,1,9,1) 100%)", */
		position: "relative",
		borderRadius: "0 !important",
		boxShadow: "none",
		zIndex: -1,
	},
	imgFon: {
		position: "fixed",
		backgroundImage: " url(./img/newfon.png)",
		width: "100%",
		height: "100%",
		backgroundSize: "cover",
		backgroundPosition: "center center",
		backgroundRepeat: "no-repeat",
		zIndex: -1,
	},
	home: {
		background:
			"linear-gradient(180deg,#3e2696 30%,#351e8a 0,#351e8a 70%,#472ea5 0)",
		color: "#FFF",
		height: "100vh",
		borderRadius: "0 !important",
		boxShadow: "none",
		position: "relative",
		[theme.breakpoints.between("xs", "sm")]: {
			minHeight: "100vh",
			height: "100%",
		},
	},
	box: {
		backgroundColor: "#1D0575",
		borderRadius: "25px",
		position: "absolute",
		height: "calc(100vh - 200px)",
		top: "50%",
		transform: "translateY(-50%)",
		width: "calc(100% - 32px)",
		[theme.breakpoints.between("xs", "sm")]: {
			position: "relative",
			height: "100%",
			width: "100%",
			top: "0",
			transform: "none",
		},
	},
	listTedy: {
		height: "calc(100vh - 200px)",
		display: "flex",
		[theme.breakpoints.between("xs", "sm")]: {
			width: "100%",
			height: "100%",
			flexWrap: "wrap",
		},
	},
	imgTedy: {
		height: "100%",
		minWidth: "250px",
		maxWidth: "100%",
		objectFit: "cover",
		borderRadius: `25px 0 0 25px`,
		[theme.breakpoints.between("xs", "sm")]: {
			borderRadius: `25px 25px 0 0`,
			width: "100%",
		},
	},
	listInfo: {
		display: "flex",
		flexDirection: "column",
		minWidth: "300px",
	},
	"listInfoItem:not(:last-child)": {
		marginBottom: "5px",
		borderBottom: "1px solid #6049b0;",
	},
	buttonWall: {
		width: "100%",
		height: "60px",
		marginTop: "10px",
		marginBottom: "5px",
		background: "linear-gradient(180deg, #604ae5 0%, #813eee 100%)",
		color: "white",
		fontSize: "16px",
		fontWeight: "bold",
	},
	container: {
		[theme.breakpoints.down("sm")]: {
			width: "100%",
		},
		[theme.breakpoints.up("md")]: {
			width: "100%",
		},
		[theme.breakpoints.up("lg")]: {
			width: "960px",
		},
		[theme.breakpoints.up("xl")]: {
			width: "1200px",
		},
	},
	botText: {
		textAlign: "center",
		position: "absolute",
		bottom: "50px",
		left: "50%",
		width: "fit-content",
		transform: "translateX(-50%)",
		[theme.breakpoints.between("xs", "sm")]: {
			position: "relative",
			bottom: "0",
			left: "0",
			transform: "none",
			marginTop: "20px",
		},
	},
	paper: {
		background: "#FFF !important",
		position: "relative",
		minHeight: "100vh",
		padding: "50px 0",
		boxShadow: "none !important",
		"&::before": {
			content: '""',
			position: "absolute",
			background: "#FFF url(/img/mint.svg) !important",
			backgroundSize: "cover",
			backgroundPosition: "center",
			backgroundRepeat: "no-repeat",
			top: "2%",
			left: "25%",
			width: "47%",
			height: "98%",
			[theme.breakpoints.between("xs", "sm")]: {
				top: "10%",
				left: "12%",
				width: "80%",
				height: "45%",
			},
			[theme.breakpoints.between("sm", "md")]: {
				top: "0%",
				left: "3%",
				width: "100%",
				height: "90%",
			},
			[theme.breakpoints.up("xl")]: {
				top: "3%",
				left: "25%",
				width: "53%",
				height: "90%",
			},
		},
	},
	paperFo: {
		background: "#39228B !important",
		position: "relative",
		minHeight: "100vh",
		boxShadow: "none !important",
	},
	foot: {
		backgroundColor: "#000 !important",
		borderRadius: "0 !important",
		display: "flex",
		"& > div": {
			display: "flex",
			flexWrap: "wrap",
		},
	},
	imgMint: {
		width: "160px",
		objectFit: "cover",
	},
	textH3: {
		"& > div": {
			marginRight: "auto",
		},
	},
	faq1: {
		height: "100%",
		width: "100%",
		position: "absolute",
	},
	w100: {
		width: "100% !important",
	},
}))

const Home = (props: HomeProps) => {
  const styles = useStyles()
  const [open, setOpen] = useState<boolean>(false)
  const [isUserMinting, setIsUserMinting] = useState(false);
  const [candyMachine, setCandyMachine] = useState<CandyMachineAccount>();
  const [alertState, setAlertState] = useState<AlertState>({
    open: false,
    message: '',
    severity: undefined,
  });

  const rpcUrl = props.rpcHost;
  const wallet = useWallet();

  const anchorWallet = useMemo(() => {
    if (
      !wallet ||
      !wallet.publicKey ||
      !wallet.signAllTransactions ||
      !wallet.signTransaction
    ) {
      return;
    }

    return {
      publicKey: wallet.publicKey,
      signAllTransactions: wallet.signAllTransactions,
      signTransaction: wallet.signTransaction,
    } as anchor.Wallet;
  }, [wallet]);

  
  const refreshCandyMachineState = useCallback(async () => {
    if (!anchorWallet) {
      return;
    }

    if (props.candyMachineId) {
      try {
        const cndy = await getCandyMachineState(
          anchorWallet,
          props.candyMachineId,
          props.connection,
        );
        setCandyMachine(cndy);
      } catch (e) {
        console.log('There was a problem fetching Candy Machine state');
        console.log(e);
      }
    }
  }, [anchorWallet, props.candyMachineId, props.connection]);

  const onMint = async () => {
    try {
      setIsUserMinting(true);
      document.getElementById('#identity')?.click();
      if (wallet.connected && candyMachine?.program && wallet.publicKey) {
        const mintTxId = (
          await mintOneToken(candyMachine, wallet.publicKey)
        )[0];

        let status: any = { err: true };
        if (mintTxId) {
          status = await awaitTransactionSignatureConfirmation(
            mintTxId,
            props.txTimeout,
            props.connection,
            true,
          );
        }

        if (status && !status.err) {
          setAlertState({
            open: true,
            message: 'Congratulations! Mint succeeded!',
            severity: 'success',
          });
        } else {
          setAlertState({
            open: true,
            message: 'Mint failed! Please try again!',
            severity: 'error',
          });
        }
      }
    } catch (error: any) {
      let message = error.msg || 'Minting failed! Please try again!';
      if (!error.msg) {
        if (!error.message) {
          message = 'Transaction Timeout! Please try again.';
        } else if (error.message.indexOf('0x137')) {
          message = `SOLD OUT!`;
        } else if (error.message.indexOf('0x135')) {
          message = `Insufficient funds to mint. Please fund your wallet.`;
        }
      } else {
        if (error.code === 311) {
          message = `SOLD OUT!`;
          window.location.reload();
        } else if (error.code === 312) {
          message = `Minting period hasn't started yet.`;
        }
      }

      setAlertState({
        open: true,
        message,
        severity: 'error',
      });
    } finally {
      setIsUserMinting(false);
    }
  };
  useEffect(() => {
    if (alertState && alertState.open && alertState.severity === 'success') {
      console.log('Minting success!');
      setTimeout(() => {refreshCandyMachineState()}, 6000)
    }
  }, [alertState, refreshCandyMachineState])
  useEffect(() => {
    refreshCandyMachineState();
  }, [
    anchorWallet,
    props.candyMachineId,
    props.connection,
    refreshCandyMachineState,
    candyMachine?.state.itemsRemaining
  ]);

  return (
    <>
          <AppBar className={styles.app} position='static'>
				<Toolbar>
					<Box sx={{ mr: "auto", display: { xs: "flex", sm: "none" } }}>
						<IconButton
							size='medium'
							edge='start'
							color='inherit'
							aria-label='menu'
							onClick={() => setOpen(true)}
							>
							<MenuIcon />
						</IconButton>
					</Box>

					<Box sx={{ mr: "auto", display: { xs: "none", sm: "flex" } }}>
						<List className={styles.list}>
							<ListItem>
								<IconButton>
									<img src='/img/logo.png' alt='logo' width={100} height={36} />
								</IconButton>
							</ListItem>
							<ListItem>
								<Link href='#home' style={{ color: "#FFF" }}>
									Home
								</Link>
							</ListItem>
							<ListItem>
								<Link href='#second' style={{ color: "#FFF" }}>
									Second
								</Link>
							</ListItem>
							<ListItem>
								<Link href='#third' style={{ color: "#FFF" }}>
									Third
								</Link>
							</ListItem>
							<ListItem>
								<Link href='#fourth' style={{ color: "#FFF" }}>
									Fourth
								</Link>
							</ListItem>
						</List>
					</Box>
					<Box sx={{ ml: "auto" }}>
						<IconButton color='inherit'>
							<Twitt />
						</IconButton>
						<IconButton color='inherit'>
							<You />
						</IconButton>
						<IconButton color='inherit'>
							<Tel />
						</IconButton>
						<IconButton color='inherit'>
							<Icon className='fab fa-medium' />
						</IconButton>
						<IconButton color='inherit'>
							<Icon className='fab fa-discord' />
						</IconButton>
					</Box>
				</Toolbar>
				<Drawer
					anchor='left'
					open={open}
					onClose={() => setOpen(false)}
					classes={{ paper: styles.drawerPaper }}>
					<div className={styles.drawerHeader}>
						<IconButton onClick={() => setOpen(false)}>
							<Chevron color='secondary' />
						</IconButton>
					</div>
					<Divider />
					<List className={styles.listD}>
						<ListItem>
							<IconButton>
								<img src='/img/logo.png' alt='logo' width={100} height={36} />
							</IconButton>
						</ListItem>
						<ListItem>
							<Link href='#home' style={{ color: "#FFF" }}>
								Home
							</Link>
						</ListItem>
						<ListItem>
							<Link href='#second' style={{ color: "#FFF" }}>
								Second
							</Link>
						</ListItem>
						<ListItem>
							<Link href='#third' style={{ color: "#FFF" }}>
								Third
							</Link>
						</ListItem>
						<ListItem>
							<Link href='#fourth' style={{ color: "#FFF" }}>
								Fourth
							</Link>
						</ListItem>
					</List>
				</Drawer>
			</AppBar>
			<Paper id='home' className={styles.first}>
				<div className={styles.imgFon} />
				<Container maxWidth={false} style={{ paddingTop: "18%" }}>
					<Fade bottom>
						<Typography
							variant='h4'
							component='h1'
							color='secondary'
							style={{ textAlign: "center" }}>
							Solano H1
						</Typography>
					</Fade>
					<Fade bottom>
						<Typography
							variant='h1'
							component='h2'
							color='secondary'
							style={{ textAlign: "center" }}>
							Solano H2
						</Typography>
					</Fade>
				</Container>
			</Paper>
			<Paper id='second' className={styles.home}>
				<Container
					maxWidth={false}
					className={styles.container}
					disableGutters
					style={{
						position: "relative",
						height: "100%",
						padding: "30px 16px",
					}}>
					<Box className={styles.box}>
						<List className={styles.listTedy} disablePadding>
							<ListItem
								disableGutters
								style={{ height: "100%", width: "100%", padding: "0" }}>
								<Fade bottom>
									<img
										src='/img/tedy.jpg'
										alt='tedy'
										className={styles.imgTedy}
									/>
								</Fade>
							</ListItem>
							<ListItem style={{ justifyContent: "center" }}>
								<Fade bottom>
									{!wallet.connected ? (
										<List className={styles.listInfo}>
											<ListItem
												className={styles["listInfoItem:not(:last-child)"]}>
												<ConnectButton>Connect Wallet</ConnectButton>
											</ListItem>
											<ListItem
												className={styles["listInfoItem:not(:last-child)"]}>
												<Typography
													variant='h6'
													component='div'
													color='secondary'
													style={{ display: "flex", width: "100%" }}>
													Price:{" "}
													<span style={{ marginLeft: "auto" }}>1 SOL</span>
												</Typography>
											</ListItem>
											<ListItem
												className={styles["listInfoItem:not(:last-child)"]}>
												<Typography
													variant='h6'
													component='div'
													color='secondary'
													style={{ display: "flex", width: "100%" }}>
													Remaining:{" "}
													<span style={{ marginLeft: "auto" }}>1 SOL</span>
												</Typography>
											</ListItem>
										</List>
									) : (
										candyMachine && (
											<List className={styles.listInfo}>
												<ListItem
													className={styles["listInfoItem:not(:last-child)"]}>
													<MintCountdown
														date={toDate(
															candyMachine?.state.goLiveDate
																? candyMachine?.state.goLiveDate
																: candyMachine?.state.isPresale
																? new anchor.BN(new Date().getTime() / 1000)
																: undefined,
														)}
														style={{ justifyContent: "flex-end" }}
														status={
															!candyMachine?.state?.isActive ||
															candyMachine?.state?.isSoldOut
																? "COMPLETED"
																: candyMachine?.state.isPresale
																? "PRESALE"
																: "LIVE"
														}
													/>
												</ListItem>
												<ListItem
													className={styles["listInfoItem:not(:last-child)"]}>
													<Typography
														variant='h6'
														component='div'
														color='secondary'
														style={{ display: "flex", width: "100%" }}>
														Price:{" "}
														<span style={{ marginLeft: "auto" }}>
															◎{" "}
															{formatNumber.asNumber(
																candyMachine?.state.price!,
															)}
														</span>
													</Typography>
												</ListItem>
												<ListItem
													className={styles["listInfoItem:not(:last-child)"]}>
													<Typography
														variant='h6'
														component='div'
														color='secondary'
														style={{ display: "flex", width: "100%" }}>
														Remaining:{" "}
														<span
															style={{
																marginLeft: "auto",
															}}>{`${candyMachine?.state.itemsRemaining}`}</span>
													</Typography>
												</ListItem>
												<ListItem
													className={styles["listInfoItem:not(:last-child)"]}>
													<MintContainer>
														{candyMachine?.state.isActive &&
														candyMachine?.state.gatekeeper &&
														wallet.publicKey &&
														wallet.signTransaction ? (
															<GatewayProvider
																wallet={{
																	publicKey:
																		wallet.publicKey ||
																		new PublicKey(CANDY_MACHINE_PROGRAM),
																	//@ts-ignore
																	signTransaction: wallet.signTransaction,
																}}
																gatekeeperNetwork={
																	candyMachine?.state?.gatekeeper
																		?.gatekeeperNetwork
																}
																clusterUrl={rpcUrl}
																options={{ autoShowModal: false }}>
																<MintButton
																	candyMachine={candyMachine}
																	isMinting={isUserMinting}
																	onMint={onMint}
																/>
															</GatewayProvider>
														) : (
															<MintButton
																candyMachine={candyMachine}
																isMinting={isUserMinting}
																onMint={onMint}
															/>
														)}
													</MintContainer>
												</ListItem>
											</List>
										)
									)}
								</Fade>
							</ListItem>
						</List>
					</Box>
					<Typography
						variant='h5'
						component='h2'
						className={styles.botText}
						color='secondary'>
