import { useEffect, useMemo, useState, useCallback } from 'react';
import * as anchor from '@project-serum/anchor';

import styled from 'styled-components';
import { Container, Snackbar, makeStyles, Box,
	List,
	ListItem,
	Typography, 
  Theme,
  AppBar,
  Toolbar, IconButton, Icon, Link, Drawer, Divider, Grid, Accordion, AccordionSummary, AccordionDetails
} from '@material-ui/core';
import MenuIcon from "@material-ui/icons/Menu"
import Twitt from "@material-ui/icons/Twitter"
import You from "@material-ui/icons/YouTube"
import Tel from "@material-ui/icons/Telegram"
import Chevron from "@material-ui/icons/ChevronLeft"
import Paper from '@material-ui/core/Paper';
import Alert from '@material-ui/lab/Alert';
import ExpandMoreIcon from "@material-ui/icons/ExpandMore"
import Fade from "react-reveal/Fade"
import { useWallet } from '@solana/wallet-adapter-react';
import { WalletDialogButton } from '@solana/wallet-adapter-material-ui';
import {
  awaitTransactionSignatureConfirmation,
  CandyMachineAccount,
  CANDY_MACHINE_PROGRAM,
  getCandyMachineState,
  mintOneToken,
} from './candy-machine';
import { AlertState, formatNumber, toDate } from './utils';
import { MintCountdown } from './MintCountdown';
import { GatewayProvider } from '@civic/solana-gateway-react';
import { PublicKey } from '@solana/web3.js';
import { MintButton } from './MintButton';

const ConnectButton = styled(WalletDialogButton)`
  width: 100%;
  height: 60px;
  margin-top: 10px;
  margin-bottom: 5px;
  background: linear-gradient(180deg, #604ae5 0%, #813eee 100%);
  color: white;
  font-size: 16px;
  font-weight: bold;
`;

export interface HomeProps {
  candyMachineId?: anchor.web3.PublicKey;
  connection: anchor.web3.Connection;
  startDate: number;
  txTimeout: number;
  rpcHost: string;
}
const MintContainer = styled.div``

let useStyles = makeStyles((theme: Theme) => ({
	app: {
		width: "100%",
	},
	list: {
		display: "flex",
		flexGrow: 1,
	},
	listD: {
		display: "flex",
		margin: "15px auto !important",
		flexDirection: "column",
		height: "35px",
		width: "100px",
		textAlign: "center",
	},
	drawerHeader: {
		display: "flex",
		alignItems: "center",
		padding: theme.spacing(0, 1),
		...theme.mixins.toolbar,
		justifyContent: "flex-end",
	},
	drawerPaper: {
		width: "100%",
		backgroundColor: "rgba(55,55,55, .6)",
		backdropFilter: "blur(2px)",
	},
	first: {
		minHeight: "100vh",
		background:
			"radial-gradient(circle, rgba(2,1,9,1) 0%, rgba(43,22,49,1) 50%, rgba(2,1,9,1) 100%)",
		position: "relative",
		borderRadius: "0 !important",
		boxShadow: "none",
		zIndex: 0,
	},
	imgFon: {
		position: "absolute",
		backgroundImage: " url(./img/first.webp)",
		width: "100%",
		height: "100%",
		backgroundSize: "contain",
		backgroundPosition: "center center",
		backgroundRepeat: "no-repeat",
		zIndex: -1,
	},
	home: {
		background:
			"linear-gradient(180deg,#3e2696 30%,#351e8a 0,#351e8a 70%,#472ea5 0)",
		color: "#FFF",
		height: "100vh",
		borderRadius: "0 !important",
		boxShadow: "none",
		position: "relative",
		[theme.breakpoints.between("xs", "sm")]: {
			minHeight: "100vh",
			height: "100%",
		},
	},
	box: {
		backgroundColor: "#1D0575",
		borderRadius: "25px",
		position: "absolute",
		height: "calc(100vh - 200px)",
		top: "50%",
		transform: "translateY(-50%)",
		width: "calc(100% - 32px)",
		[theme.breakpoints.between("xs", "sm")]: {
			position: "relative",
			height: "100%",
			width: "100%",
			top: "0",
			transform: "none",
		},
	},
	listTedy: {
		height: "calc(100vh - 200px)",
		display: "flex",
		[theme.breakpoints.between("xs", "sm")]: {
			width: "100%",
			height: "100%",
			flexWrap: "wrap",
		},
	},
	imgTedy: {
		height: "100%",
		minWidth: "250px",
		maxWidth: "100%",
		objectFit: "cover",
		borderRadius: `25px 0 0 25px`,
		[theme.breakpoints.between("xs", "sm")]: {
			borderRadius: `25px 25px 0 0`,
			width: "100%",
		},
	},
	listInfo: {
		display: "flex",
		flexDirection: "column",
		minWidth: "300px",
	},
	"listInfoItem:not(:last-child)": {
		marginBottom: "5px",
		borderBottom: "1px solid #6049b0;",
	},
	buttonWall: {
		width: "100%",
		height: "60px",
		marginTop: "10px",
		marginBottom: "5px",
		background: "linear-gradient(180deg, #604ae5 0%, #813eee 100%)",
		color: "white",
		fontSize: "16px",
		fontWeight: "bold",
	},
	container: {
		[theme.breakpoints.down("sm")]: {
			width: "100%",
		},
		[theme.breakpoints.up("md")]: {
			width: "100%",
		},
		[theme.breakpoints.up("lg")]: {
			width: "960px",
		},
		[theme.breakpoints.up("xl")]: {
			width: "1200px",
		},
	},
	botText: {
		textAlign: "center",
		position: "absolute",
		bottom: "50px",
		left: "50%",
		width: "fit-content",
		transform: "translateX(-50%)",
		[theme.breakpoints.between("xs", "sm")]: {
			position: "relative",
			bottom: "0",
			left: "0",
			transform: "none",
			marginTop: "20px",
		},
	},
	paper: {
		background: "#FFF !important",
		position: "relative",
		minHeight: "100vh",
		padding: "50px 0",
		boxShadow: "none !important",
		"&::before": {
			content: '""',
			position: "absolute",
			background: "#FFF url(/img/mint.svg) !important",
			backgroundSize: "cover",
			backgroundPosition: "center",
			backgroundRepeat: "no-repeat",
			top: "2%",
			left: "25%",
			width: "47%",
			height: "98%",
			[theme.breakpoints.between("xs", "sm")]: {
				top: "10%",
				left: "12%",
				width: "80%",
				height: "45%",
			},
			[theme.breakpoints.between("sm", "md")]: {
				top: "0%",
				left: "3%",
				width: "100%",
				height: "90%",
			},
			[theme.breakpoints.up("xl")]: {
				top: "3%",
				left: "25%",
